import { useState, useEffect } from 'react';
import { API_URL } from '../../api';

import ProjectList from '../../components/project-list/ProjectList';

import arrow from '../../images/a1.png';
import arrow2 from '../../images/a2.png';
import logo1 from '../../images/logo1.png';
import lupa from '../../images/lupa.png';

function reload() {
	window.location.reload(false);
}

function goUp() {
    window.scrollTo({
        top: 0,
        behavior: 'smooth',
    });
  }

  function goDown() {
	window.scrollTo({
		top: document.body.offsetHeight, 
		behavior: 'smooth',
	  });
  }

const Homepage = () => {
	const [projects, setProjects] = useState(null);
	const [categories, setCategories] = useState(null);
	const [loading, setLoading] = useState(true);

	const fetchProjects = async (category = '') => {
		setLoading(true);
		await fetch(API_URL + `conto${category}`)
			.then(response => response.json())
			.then(result => {
				let resultFinal = [];
				for (const proj of result) {
					console.log(proj);
					resultFinal.push(proj);
					break;
				}
				var jsony = JSON.stringify(result);
				setProjects(result)
				setLoading(false)
			});
	}

	//console.log(projects);

	const fetchCategories = async () => {
		await fetch(API_URL + 'categories')
			.then(response => response.json())
			.then(result => {
				setCategories(result)
			});
	}

	const searching = async () => {
		setLoading(true);
		await fetch(API_URL + `conto`)
			.then(response => response.json())
			.then(result => {
				setProjects(result)
				setLoading(false)
			});
	}

	useEffect(() => {
		fetchProjects();
		fetchCategories();
	}, [])

	if (!projects || !categories) {
		return null;
	}

	const projectList = !loading ? (
		<ProjectList projects={projects} />
	) : (
		<h2>Loading...</h2>
	)

	return (
		<>
		<section>
		<img id='logo' src={logo1} onClick={reload}></img>
		<input  type="text" id="search" placeholder="pesquisar"></input>
		<img id='lupa' src={lupa}></img>
		  <div className="storyList">
		  {projectList}
		  </div>
		  <img className='arrow' id='arrowUp' src={arrow} onClick={goDown}></img>
		  <p id="slogan">quem conta um conto, acrescenta um ponto</p>
		</section>
		<section id='info'>
		<img className='arrow' id='arrowDown' src={arrow2} onClick={goUp}></img>
  		<p id="info">.Conto propõe uma forma alternativa de contar histórias para crianças. É uma coleção de reintrepretações de contos clássicos, do texto às ilustrações, com uma forte componente didática e interativa. Surge em contexto académico, na cadeira de Desenvolvimento Web do Mestrado em Design e Multimédia na Universidade de Coimbra. 
			Resulta do trabalho de <a className="linksPessoais" href="https://www.instagram.com/__inesazevedo__/" target="_blank" rel="noopener noreferrer">Inês Azevedo</a>, <a className="linksPessoais" href="https://www.instagram.com/mariana_pombo7/" target="_blank" rel="noopener noreferrer">Mariana Pombo</a> e <a className="linksPessoais" href="https://www.instagram.com/thomaspfresco/" target="_blank" rel="noopener noreferrer">Thomas Fresco</a>.</p>
		</section>
		</>
		/*<div>
			<h2>Projects</h2>
			<button className="filter-button" onClick={() => fetchProjects()}>All Projects</button>
			{categories.map(category => (
				<button 
					className="filter-button" 
					key={category.id} 
					onClick={() => fetchProjects(`?categories=${category.id}`)}
				>{category.name}</button>
				))
			}
			<div>
			{projectList}
			</div>
		</div>*/
	)
}

export default Homepage
