import { useState, useEffect } from 'react';
import { API_URL } from '../../api';

import ProjectList from '../../components/project-list/ProjectList';

const Homepage = () => {
	const [projects, setProjects] = useState(null);
	const [categories, setCategories] = useState(null);
	const [loading, setLoading] = useState(true);

	const fetchProjects = async(category = '') => {
		setLoading(true);
		await fetch(API_URL + `conto${category}`)
				.then(response => response.json())
				.then(result => {
					setProjects(result)
					setLoading(false)
				});
	}

	//console.log(projects[0].content.rendered.split(" ")[7].split('"')[1]);

	const fetchCategories = async() => {
		await fetch(API_URL + 'categories')
				.then(response => response.json())
				.then(result => {
					setCategories(result)
				});
	}

	useEffect(() => {
		fetchProjects();
		fetchCategories();
	}, [])

	if (!projects || !categories) {
		return null;
	}

	const projectList = !loading ? (
			<ProjectList projects={projects} />
		) : (
			<h2>Loading...</h2>
		) 

	return (
		<div >
<div className="content">
    <div className="smooth">
        <section id="one">

		<div>
			<h2>Projects</h2>
			<button className="filter-button" onClick={() => fetchProjects()}>All Projects</button>
			{categories.map(category => (
				<button 
					className="filter-button" 
					key={category.id} 
					onClick={() => fetchProjects(`?categories=${category.id}`)}
				>{category.name}</button>
				))
			}
			<div>
			{projectList}
			</div>


		</div>

        </section>
        <section id="two">
            <h3>two</h3>
			<img id='arrowDown' src='src/images/a.png'></img>
        </section>
        <section id="three">
            <h3>three</h3>
        </section>
        <section id="four">
            <h3>four</h3>
        </section>
    </div>
</div>
	</div>
		/*<div>
			<h2>Projects</h2>
			<button className="filter-button" onClick={() => fetchProjects()}>All Projects</button>
			{categories.map(category => (
				<button 
					className="filter-button" 
					key={category.id} 
					onClick={() => fetchProjects(`?categories=${category.id}`)}
				>{category.name}</button>
				))
			}
			<div>
			{projectList}
			</div>
		</div>*/
		)
}

export default Homepage
