	const [conto, setProjectInfo] = useState(null);
	const [sections, setSection] = useState(null);
	const { slug } = useParams();

	useEffect(() => {
		fetch(API_URL + `conto?slug=${slug}`)
		.then(response => response.json())
		.then(result => {
			//console.log(result[0].content.rendered);
