import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { API_URL } from '../../api';

import StorySection from '../../components/story-sections/StorySection.js';

const Project = () => {
	const [conto, setProjectInfo] = useState(null);
	const { slug } = useParams();

	function generateSections() {
		const sections = [];

	for (let i = 0; i < data.length; i++) {
		contos.push(<div>
		<img class="capa" src={data[i].content.rendered.split(" ")[6].split('"')[1]}></img>
		<p class="titulo">{data[i].slug}</p>
		</div>);

		return sections;
	}

	}

	useEffect(() => {
		fetch(API_URL + `conto?slug=${slug}`)
		.then(response => response.json())
		.then(result => {
			console.log(result[0].content.rendered.split('\n'));
			setProjectInfo(result)
		});
	}, [slug])

	if (!conto) {
		return null;
	}

	return (
		<>
		<div>
			<p>{conto[0].title.rendered}</p>
			{generateSections}
		</div>
		</>
		)


	//<img src={conto[0]?.acf?.image} width='100' alt={conto[0]?.acf?.client} />
}

export default Project;
